import React, { useState, useEffect, useRef, useMemo } from "react";
import axios from "axios";

const userColors = [
  "#198754", "#a7c7e7", "#f4a261", "#e76f51", "#2a9d8f", "#264653"
];

const getUserColor = (userId) => {
  if (!userId) return "#ccc";
  const index =
    userId
      .toString()
      .split("")
      .reduce((acc, char) => acc + char.charCodeAt(0), 0) % userColors.length;
  return userColors[index];
};

const ChatWindow = ({ orderId, onClose }) => {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState("");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [recipientId, setRecipientId] = useState(null);
  const [chatId, setChatId] = useState(null);
  const token = localStorage.getItem("token");
  const messagesEndRef = useRef(null);

  const currentUserId = useMemo(() => {
    if (!token) return null;
    try {
      const payload = JSON.parse(atob(token.split(".")[1]));
      return payload.sub;
    } catch {
      return null;
    }
  }, [token]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    const fetchChat = async () => {
      setLoading(true);
      try {
        const { data } = await axios.get(
          `http://127.0.0.1:8000/api/user/order/chat/${orderId}`,
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );

        const chatData = data.chat;
        setChatId(chatData.id);
        const fetchedMessages = chatData.messages || [];
        setMessages(fetchedMessages);

        if (fetchedMessages.length > 0) {
          const firstIncoming = fetchedMessages.find(
            (msg) => msg.sender_id !== currentUserId
          );
          if (firstIncoming) {
            setRecipientId(firstIncoming.sender_id);
          } else {
            const fallback = fetchedMessages[0];
            const recipient =
              fallback.sender_id === currentUserId
                ? fallback.recipient_id
                : fallback.sender_id;
            setRecipientId(recipient);
          }
        }
      } catch {
        setError("Ошибка загрузки сообщений.");
      } finally {
        setLoading(false);
      }
    };

    if (orderId && token) fetchChat();
  }, [orderId, token, currentUserId]);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSendMessage = async (e) => {
    e.preventDefault();
    if (!newMessage.trim()) return;

    try {
      const { data } = await axios.post(
        `http://127.0.0.1:8000/api/user/order/chat/${orderId}/message`,
        { content: newMessage, recipient_id: recipientId },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      const newMsg = data.message;

      if (!recipientId) {
        const recipient =
          newMsg.sender_id === currentUserId
            ? newMsg.recipient_id
            : newMsg.sender_id;
        setRecipientId(recipient);
      }

      setMessages((prev) => [...prev, newMsg]);
      setNewMessage("");
    } catch {
      setError("Ошибка при отправке сообщения.");
    }
  };

  return (
    <div
      style={{
        width: "100%",
        maxWidth: "600px",
        margin: "0 auto",
        border: "1px solid #ccc",
        borderRadius: "8px",
        display: "flex",
        flexDirection: "column",
        height: "80vh",
      }}
    >
      <div
        style={{
          padding: "10px",
          borderBottom: "1px solid #ccc",
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
          backgroundColor: "#f8f9fa",
        }}
      >
        <h5 style={{ margin: 0 }}>Чат по заказу #{orderId}</h5>
        <button onClick={onClose} style={{ border: "none", background: "none" }}>
          ❌
        </button>
      </div>

      <div
        style={{
          flex: 1,
          overflowY: "auto",
          display: "flex",
          flexDirection: "column",
          padding: "10px",
        }}
      >
        {loading ? (
          <p>Загрузка...</p>
        ) : messages.length > 0 ? (
          messages.map((msg) => {
            const isCurrentUser = msg.sender_id === currentUserId;
            const bgColor = getUserColor(msg.sender_id);
            const alignment = isCurrentUser ? "flex-end" : "flex-start";
            const textAlign = isCurrentUser ? "right" : "left";

            return (
              <div
                key={msg.id}
                style={{
                  alignSelf: alignment,
                  backgroundColor: bgColor,
                  color: "#fff",
                  borderRadius: "12px",
                  padding: "10px",
                  margin: "8px 0",
                  maxWidth: "70%",
                  wordBreak: "break-word",
                  boxShadow: "0 2px 6px rgba(0,0,0,0.1)",
                  textAlign,
                }}
              >
                <strong>
                  {msg.sender?.name || `Пользователь #${msg.sender_id}`}:
                </strong>
                <p style={{ margin: "5px 0" }}>{msg.content}</p>
                {msg.created_at && (
                  <span style={{ fontSize: "0.75rem", opacity: 0.8 }}>
                    {new Date(msg.created_at).toLocaleTimeString()}
                  </span>
                )}
              </div>
            );
          })
        ) : (
          <div>Сообщений пока нет.</div>
        )}
        <div ref={messagesEndRef}></div>
      </div>

      <form onSubmit={handleSendMessage} style={{ padding: "10px" }}>
        <input
          type="text"
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          placeholder="Введите сообщение..."
          style={{
            width: "100%",
            padding: "10px",
            marginBottom: "10px",
            borderRadius: "6px",
            border: "1px solid #ccc",
          }}
        />
        <button
          type="submit"
          style={{
            width: "100%",
            padding: "10px",
            backgroundColor: "#0d6efd",
            color: "#fff",
            border: "none",
            borderRadius: "6px",
          }}
        >
          Отправить
        </button>
        {error && (
          <div style={{ color: "red", marginTop: "10px" }}>{error}</div>
        )}
      </form>
    </div>
  );
};

export default ChatWindow;
